data.user-signup#User signup:
To create a user
// lib/user.js
import db from './db';

export function createUser(email, password) {
const result = db
.prepare('INSERT INTO users (email, password) VALUES (?, ?)')
.run(email, password);
return result.lastInsertRowid;
}
When creating a user, his passwords need to be hashed, so
// lib/hash.js
import crypto from 'node:crypto';

export function hashUserPassword(password) {
const salt = crypto.randomBytes(16).toString('hex');

const hashedPassword = crypto.scryptSync(password, salt, 64);
return hashedPassword.toString('hex') + ':' + salt;
}

export function verifyPassword(storedPassword, suppliedPassword) {
const [hashedPassword, salt] = storedPassword.split(':');
const hashedPasswordBuf = Buffer.from(hashedPassword, 'hex');
const suppliedPasswordBuf = crypto.scryptSync(suppliedPassword, salt, 64);
return crypto.timingSafeEqual(hashedPasswordBuf, suppliedPasswordBuf);
}
Need an action to handle the interaction
// actions/auth-actions.js

'use server';

import { hashUserPassword } from '@/lib/hash';
import { createUser } from '@/lib/user';
import { redirect } from 'next/navigation';

export async function signUp(prevState, formData) {
const email = formData.get('email');
const password = formData.get('password');
// validate
let errors = {};
if (!email.includes('@')) {
errors.email = 'Email is invalid';
}
if (password.trim().length < 8) {
errors.password = 'Password is invalid';
}

if (Object.keys(errors).length > 0) {
return {
errors,
};
}

const hashedPassword = hashUserPassword(password);

try {
createUser(email, hashedPassword);
} catch (error) {
if (error.code === 'SQLITE_CONSTRAINT_UNIQUE') {
return {
errors: {
email: 'Email already exists',
},
};
}
throw error;
}
redirect('/training');
}
