In lib/auth.js
export async function destroySession() {
const { session } = await verifyAuth();
if (!session) {
return {
error: 'Unauthorized',
};
}
await lucia.invalidateSession(session.id);
const sessionCookie = lucia.createBlankSessionCookie();
cookies().set(
sessionCookie.name,
sessionCookie.value,
sessionCookie.attributes
);
}
